mapper.select:
select MedicineId,MedicineName,CommonName,TinySpecification, (select ManufacturerName from manufacturer where ManufacturerId=m.ManufacturerId) as manufacturerName, (select DosageFormsName from dosageforms where DosageFormsId=m.DosageFormsId) as dosageFormsName, (select MedicineKindName from medicinekind where MedicineKindId=m.MedicineKindId) as medicineKindName, (select MCKName from medicinecustomkind where MCKId=m.MCKId) as mCKName, StandardCode from medicine m
m.MedicineName like CONCAT ('%',#{medicineName},'%')
limit #{pageIndex},#{pageSize}
